mapper.select:
	select * from (select * from chat_msg 
                        where room_no in
                            (select room_no from chat_room c
                            where from_id=#{userId} or to_id=#{userId})
                    ) cr
	where cr.msg_read=0 and cr.user_id!=#{userId}

	select count(*) from (select * from chat_msg 
                        where room_no in
                            (select room_no from chat_room c
                            where from_id=#{userId} or to_id=#{userId})
                    ) cr
	where cr.msg_read=0 and cr.user_id!=#{userId}
